document.addEventListener("DOMContentLoaded", function () {
    alert("JavaScript is geladen!"); // Test of de script werkt

    const form = document.getElementById("actionForm");

    if (!form) {
        console.error("⚠️ Formulier niet gevonden!");
        return;
    }

    console.log("✅ Formulier gevonden!");

    form.addEventListener("submit", function(event) {
        event.preventDefault();

        const action = document.getElementById("action").value;
        const details = document.getElementById("details").value;

        console.log(`Verstuur actie: ${action}, Details: ${details}`);

        fetch("http://localhost:3000/action", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ action, details })
})

        .then(response => {
            if (!response.ok) {
                throw new Error(`Server error: ${response.status}`);
            }
            return response.json();
        })
        .then(data => {
            console.log("✅ Server response:", data);
            alert(`Response: ${data.message}`);
        })
        .catch(error => console.error("❌ Error:", error));
        
    });
});


